// application
import { vehicleApi } from '~/api';
import { GARAGE_SET_CURRENT, GARAGE_SET_ITEMS } from '~/store/garage/garageActionTypes';
export function garageSetItems(items) {
  return {
    type: GARAGE_SET_ITEMS,
    payload: items
  };
}
export function garageSetCurrent(vehicleId) {
  return {
    type: GARAGE_SET_CURRENT,
    payload: vehicleId
  };
}
export function loadUserVehicles() {
  return dispatch => vehicleApi.getUserVehicles().then(items => {
    dispatch(garageSetItems(items));
  });
}
export function garageAddItem(vehicleId) {
  return async dispatch => {
    await vehicleApi.addUserVehicle(vehicleId);
    return dispatch(loadUserVehicles());
  };
}
export function garageRemoveItem(vehicleId) {
  return async dispatch => {
    await vehicleApi.removeUserVehicle(vehicleId);
    return dispatch(loadUserVehicles());
  };
}
