// react
import { useMemo, useState } from 'react'; // third-party

import { useForm } from 'react-hook-form'; // application

import { useAsyncAction } from '~/store/hooks';
import { useUserSignIn } from '~/store/user/userHooks';
export function useSignInForm(options = {}) {
  const signIn = useUserSignIn();
  const {
    onSuccess
  } = options;
  const {
    0: serverError,
    1: setServerError
  } = useState(null);
  const methods = useForm({
    defaultValues: {
      email: 'royalautopartsmarket@example.com',
      password: '123456',
      remember: false
    }
  });
  const {
    handleSubmit
  } = methods;
  const [submit, submitInProgress] = useAsyncAction(data => {
    setServerError(null);
    return signIn(data.email, data.password).then(() => {
      if (onSuccess) {
        onSuccess();
      }
    }, error => {
      setServerError(`ERROR_API_${error.message}`);
    });
  }, [signIn, setServerError, onSuccess]);
  return {
    submit: useMemo(() => handleSubmit(submit), [handleSubmit, submit]),
    submitInProgress: submitInProgress || methods.formState.isSubmitting,
    serverError,
    errors: methods.errors,
    register: methods.register,
    watch: methods.watch
  };
}
