import { ENQUIRY_CLOSE, ENQUIRY_OPEN } from '~/store/enquiry/enquiryActionTypes';

let cancelPreviousRequest = () => {};

export function enquiryOpenSuccess(product) {
  return {
    type: ENQUIRY_OPEN,
    product
  };
}
export function enquiryClose() {
  return {
    type: ENQUIRY_CLOSE
  };
}
export function enquiryOpen(product) {
  return dispatch => {
    dispatch(enquiryOpenSuccess(product));
    return new Promise(resolve => {
      resolve();
    });
  };
}
