import { error } from '~/fake-server/utils';
import { orders } from '~/fake-server/database/orders';
export function getOrdersList(options) {
  var optionsValue = options || {};
  var items = JSON.parse(JSON.stringify(orders));
  var page = optionsValue.page || 1;
  var limit = optionsValue.limit || 16;
  var sort = optionsValue.sort || 'default';
  var total = items.length;
  var pages = Math.ceil(items.length / limit);
  var from = (page - 1) * limit + 1;
  var to = page * limit;
  items = items.slice(from - 1, to);
  return Promise.resolve({
    page: page,
    limit: limit,
    sort: sort,
    total: total,
    pages: pages,
    from: from,
    to: to,
    items: items,
    products: items
  });
}
