// application
import { accountApi } from '~/api';
import { USER_SET_CURRENT } from '~/store/user/userActionTypes';
export function userSetCurrent(user) {
  return {
    type: USER_SET_CURRENT,
    payload: user
  };
}
export function userSignIn(email, password) {
  return function (dispatch) {
    return accountApi.signIn(email, password).then(function (user) {
      dispatch(userSetCurrent(user));
    });
  };
}
export function userSignUp(email, password) {
  return function (dispatch) {
    return accountApi.signUp(email, password).then(function (user) {
      dispatch(userSetCurrent(user));
    });
  };
}
export function userSignOut() {
  return function (dispatch) {
    return accountApi.signOut().then(function () {
      dispatch(userSetCurrent(null));
    });
  };
}
export function userEditProfile(data) {
  return function (dispatch) {
    return accountApi.editProfile(data).then(function (user) {
      dispatch(userSetCurrent(user));
    });
  };
}
